md-6">        <p>            <?php echo __('Country', FV_DOMAIN);?>:<br/>            <select name="countrycode">                <option value=""><?php echo __('Select', FV_DOMAIN);?></option>                <?php foreach($country_list as $code => $country):?>                    <option value="<?php echo $code;?>"><?php echo $country;?></option>                <?php endforeach;?>            </select>        </p>    </div></div><div class="row">    <div class="col-md-6">        <p class="pull-left">            <?php echo __('Expire date', FV_DOMAIN);?>:<br/>            <select name="expire_month">                <?php for($i = 1; $i <= 12; $i++):?>                    <option value="<?php echo $i < 10 ? '0' : '';?><?php echo $i;?>"><?php echo $i < 10 ? '0' : '';?><?php echo $i;?></option>                <?php endfor;?>            </select>            <select name="expire_year">                <?php for($i = date('Y'); $i <= date('Y') + 10; $i++):?>                    <option value="<?php echo $i;?>"><?php echo $i;?></option>                <?php endfor;?>            </select>        </p>        <p class="pull-right">            <?php echo __('Security code', FV_DOMAIN);?>:<br/>            <input type="text" name="cvv"/>        </p>    </div>    <div class="col-md-6">        <p>            <?php echo __('State', FV_DOMAIN);?>:<br/>            <input type="text" name="state"/>        </p>    </div></div><div class="row">    <div class="col-md-6">        <p>            <?php echo __('Phone', FV_DOMAIN);?>:<br/>            <input type="text" name="phone"/>        </p>    </div>    <div class="col-md-6">        <p>            <?php echo __('Zip code', FV_DOMAIN);?>:<br/>            <input type="text" name="zipcode"/>        </p>    </div></div>